package com.syntax.class01;

public class PracticeVariables {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		byte Bite = 10;
		short Short = 111;
		int Int = 666666666;
		long Long = 987654321123456789l;

		float Float = 11.11f;
		double Double = 6.66666666;

		String String = "Home Work";
		char Char = 'S';
		boolean DifficultTask = false;

		System.out.println("  bite value: " + Bite + ",\n  short value: " + Short + ",\n  integer value: " + Int
				+ ",\n  long value: " + Long + ",\n  float value: " + Float + ",\n  double value: " + Double + ",\n "
				+ String + "\n Char: " + Char + ",\n DifficultTask: " + DifficultTask);
	}

}
