package com.syntax.class3;

public class CompoudOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int num = 100;

		num = num + 10;
		System.out.println(num);

		num = num + 200;
		System.out.println(num);

		int myNum = 100;
		myNum += 10;
		System.out.println(myNum);

		myNum += 200;
		System.out.println(myNum);

		myNum -= 100;
		System.out.println(myNum);

		myNum /= 10;
		System.out.println(myNum);
		
		myNum*=1000;
		System.out.println(myNum);

		myNum %= 100;
		System.out.println(myNum);

	}

}
